export default function cleanRequire(path: string, opts: {
    justTry: true;
    resolve: true;
}): string | undefined;
export default function cleanRequire(path: string, opts: {
    justTry?: false;
    resolve: true;
}): string;
export default function cleanRequire<T = unknown>(path: string, opts: {
    justTry: true;
    resolve?: false;
}): T | undefined;
export default function cleanRequire<T = unknown>(path: string, opts: {
    justTry?: false;
    resolve?: false;
}): T;
